import React, { useState, useEffect, useContext } from 'react';
import { View, Text, TextInput, Button } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { useNavigation } from '@react-navigation/native';
import { ProfileContext } from './ProfileContext';

const ProfileScreen = () => {
    const [name, setName] = useState('');
    const [age, setAge] = useState('');
    const [weight, setWeight] = useState('');
    const [height, setHeight] = useState('');

    const navigation = useNavigation();
    const { updateProfileData } = useContext(ProfileContext);

    useEffect(() => {
        // Load the saved profile data from AsyncStorage when the component mounts
        loadProfile();
    }, []);

    const loadProfile = async () => {
        try {
            const profileData = await AsyncStorage.getItem('profile');
            if (profileData) {
                const { name, age, weight, height } = JSON.parse(profileData);
                setName(name);
                setAge(age);
                setWeight(weight);
                setHeight(height);
                updateProfileData({ name, age, weight, height }); // Update the profile data in context
            }
        } catch (error) {
            console.log('Error loading profile:', error);
        }
    };

    const saveProfile = async () => {
        try {
            const profileData = { name, age, weight, height };
            await AsyncStorage.setItem('profile', JSON.stringify(profileData));
            console.log('Profile saved successfully.');
            updateProfileData(profileData); // Update the profile data in context
            /* goToAgeScreen();*/ // Navigate to BmiScreen after saving the profile
        } catch (error) {
            console.log('Error saving profile:', error);
        }
    };

    const goToAgeScreen = () => {
        navigation.navigate('Age');
    };

    const goToBmiScreen = async () => {
        try {
            const profileData = { weight, height };
            await AsyncStorage.setItem('profile', JSON.stringify(profileData));
            console.log('Profile saved successfully.');
            updateProfileData(profileData); // Update the profile data in context
            navigation.navigate('BMI'); // Navigate to BMI screen
        } catch (error) {
            console.log('Error saving profile:', error);
        }
    };

    return (
        <ProfileContext.Provider value={{ name, age, weight, height, setName, setAge, setWeight, setHeight, updateProfileData }}>
            <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
                {/* Profile UI */}
                <View style={{ alignItems: 'flex-start', justifyContent: 'flex-start' }}>
                    {/* Display current profile data */}
                    <View style={{ flexDirection: 'row', alignItems: 'center', marginBottom: 10 }}>
                        <Text style={{ marginRight: 10 }}>Current Values:</Text>
                        <Text style={{ marginRight: 10 }}>Name: {name}</Text>
                        <Text style={{ marginRight: 10 }}>Age: {age}</Text>
                        <Text style={{ marginRight: 10 }}>Weight: {weight}</Text>
                        <Text>Height: {height}</Text>
                    </View>
                    <Text>Profile</Text>
                    {/* Profile form */}
                    <View style={{ alignItems: 'flex-start', justifyContent: 'flex-start', marginTop: 10 }}>
                        <View style={{ flexDirection: 'row', alignItems: 'center', marginBottom: 10 }}>
                            <Text style={{ marginRight: 10 }}>Name:</Text>
                            <TextInput
                                placeholder="Enter name"
                                value={name}
                                onChangeText={setName}
                                style={{
                                    paddingHorizontal: 10,
                                    width: 200,
                                    height: 40,
                                    borderWidth: 1,
                                    borderRadius: 5,
                                }}
                            />
                        </View>
                        <View style={{ flexDirection: 'row', alignItems: 'center', marginBottom: 10 }}>
                            <Text style={{ marginRight: 10 }}>Age:</Text>
                            <TextInput
                                placeholder="Enter age"
                                value={age}
                                onChangeText={setAge}
                                keyboardType="numeric"
                                style={{
                                    paddingHorizontal: 10,
                                    width: 200,
                                    height: 40,
                                    borderWidth: 1,
                                    borderRadius: 5,
                                }}
                            />
                        </View>
                        <View style={{ flexDirection: 'row', alignItems: 'center', marginBottom: 10 }}>
                            <Text style={{ marginRight: 10 }}>Weight:</Text>
                            <TextInput
                                placeholder="Enter weight"
                                value={weight}
                                onChangeText={setWeight}
                                keyboardType="numeric"
                                style={{
                                    paddingHorizontal: 10,
                                    width: 200,
                                    height: 40,
                                    borderWidth: 1,
                                    borderRadius: 5,
                                }}
                            />
                        </View>
                        <View style={{ flexDirection: 'row', alignItems: 'center', marginBottom: 10 }}>
                            <Text style={{ marginRight: 10 }}>Height:</Text>
                            <TextInput
                                placeholder="Enter height"
                                value={height}
                                onChangeText={setHeight}
                                keyboardType="numeric"
                                style={{
                                    paddingHorizontal: 10,
                                    width: 200,
                                    height: 40,
                                    borderWidth: 1,
                                    borderRadius: 5,
                                }}
                            />
                        </View>
                        {/* Save and navigate buttons */}
                        <View style={{ flexDirection: 'row', justifyContent: 'space-between', width: 200 }}>
                            <Button title="Save" onPress={saveProfile} />
                            <Button title="Next" onPress={goToAgeScreen} />
                        </View>
                    </View>
                    {/* BMI navigation button */}
                    <Button title="Calculate BMI" onPress={goToBmiScreen} />
                </View>
            </View>
            </ProfileContext.Provider>
            );

};

            export default ProfileScreen;
