import React, { useState, useEffect, useContext } from 'react';
import { View, Text } from 'react-native';
import { ProfileContext } from './ProfileContext';

const AgeScreen = () => {
  const { age } = useContext(ProfileContext);
  const [ageInYears, setAgeInYears] = useState('');
  const [ageInWeeks, setAgeInWeeks] = useState('');
  const [ageInDays, setAgeInDays] = useState('');

  const calculateAge = () => {
    if (age) {
      const currentDate = new Date();
      const birthDate = new Date(age); // Assuming the age field contains the birthdate

      // Calculate age in years
      const ageInYears = currentDate.getFullYear() - birthDate.getFullYear();

      // Calculate age in weeks
      const millisecondsPerWeek = 7 * 24 * 60 * 60 * 1000;
      const ageInWeeks = Math.floor(
        (currentDate.getTime() - birthDate.getTime()) / millisecondsPerWeek
      );

      // Calculate age in days
      const millisecondsPerDay = 24 * 60 * 60 * 1000;
      const ageInDays = Math.floor(
        (currentDate.getTime() - birthDate.getTime()) / millisecondsPerDay
      );

      setAgeInYears(ageInYears);
      setAgeInWeeks(ageInWeeks);
      setAgeInDays(ageInDays);
    }
  };

  useEffect(() => {
    calculateAge();
  }, [age]);

  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      {/* Display age calculations */}
      <Text>Age: {age}</Text>
      <Text>Age in Years: {ageInYears}</Text>
      <Text>Age in Weeks: {ageInWeeks}</Text>
      <Text>Age in Days: {ageInDays}</Text>
    </View>
  );
};

export default AgeScreen;
