import React, { useState, useContext } from 'react';
import { View, Text } from 'react-native';
import { ProfileContext } from './ProfileContext';

const AgeScreen = () => {
  const { profileData } = useContext(ProfileContext);
  const [ageInYears, setAgeInYears] = useState('');
  const [ageInWeeks, setAgeInWeeks] = useState('');
  const [ageInDays, setAgeInDays] = useState('');

  // Access the age from the profile data
  const age = profileData?.age || '';

  const calculateAge = () => {
    // Perform age calculations
    // ...

    setAgeInYears(/* Calculated age in years */);
    setAgeInWeeks(/* Calculated age in weeks */);
    setAgeInDays(/* Calculated age in days */);
  };

  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      {/* Display age calculations */}
      <Text>Age: {age}</Text>
      <Text>Age in Years: {ageInYears}</Text>
      <Text>Age in Weeks: {ageInWeeks}</Text>
      <Text>Age in Days: {ageInDays}</Text>

      {/* Calculate age button */}
      <Button title="Calculate Age" onPress={calculateAge} />
    </View>
  );
};

export default AgeScreen;
